mapper.select:
SELECT SUM(amount) FROM orders


		create_time >= #{start_time}


		create_time <= #{end_time}


		shop_id = #{shop_id}


;

 		SELECT SQL_CALC_FOUND_ROWS
 			o.*,
 			p.id AS pid, p.name,
 			a.id AS aid, a.phone, a.name as aname, a.address
 		FROM 
 			orders AS o
 			LEFT JOIN product AS p ON p.id = o.product_id
 			LEFT JOIN address AS a ON a.id = o.address_id


 				o.shop_id = #{shop_id}



 			limit #{limit} offset{#offset}

 		;

		SELECT FOUND_ROWS() as count;

SELECT SQL_CALC_FOUND_ROWS o.*, p.name as pname, p.cover_img, s.name as sname, a.name as aname, a.phone as aphone, a.address FROM orders AS o INNER JOIN product AS p ON p.id = o.product_id INNER JOIN shop AS s ON s.id = o.shop_id LEFT JOIN address AS a ON a.id = o.address_id
WHERE o.user_id = #{user_id}
limit #{limit} offset{#offset}
;
SELECT FOUND_ROWS() as count;
